mapper.select:
SELECT 

FROM ironfo_information_hardware a

WHERE a.id = #{id}

SELECT 

FROM ironfo_information_hardware a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ironfo_information_hardware a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ironfo_information_hardware SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
